from flask import Flask, render_template, jsonify, request
from flask_sqlalchemy import SQLAlchemy

app = Flask(__name__)
app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///site.db'
db = SQLAlchemy(app)

class TVShow(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    serial = db.Column(db.String(100), nullable=False)
    description = db.Column(db.Text, nullable=False)

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/load_data/<int:page>')
def load_data(page):
    items_per_page = 15
    start_index = (page - 1) * items_per_page
    end_index = start_index + items_per_page
    page_data = TVShow.query.slice(start_index, end_index).all()

    data = [{'serial': show.serial, 'description': show.description} for show in page_data]
    return jsonify({'data': data})

@app.route('/add_data', methods=['POST'])
def add_data():
    new_serial = request.json['serial']
    new_description = request.json['description']

    new_show = TVShow(serial=new_serial, description=new_description)
    db.session.add(new_show)
    db.session.commit()

    return jsonify({'success': True})

with app.app_context():
    db.drop_all()
    db.create_all()

